declareField(peripheralName = CCM, registerName = MODE, fieldName = DATARATE, bitOffset = 16, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Radio data rate that the CCM shall run synchronous with")
declareField(peripheralName = CCM, registerName = MODE, fieldName = LENGTH, bitOffset = 24, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Packet length configuration")
