declareField(peripheralName = WDT, registerName = TASKS_START, fieldName = TASKS_START, bitOffset = 0, bitWidth = 1, readAccess = false, writeAccess = true, fieldDesc = "Start the watchdog")
declareRegister(peripheralName = WDT, registerName = EVENTS_TIMEOUT, addressOffset = 0x100'u32, readAccess = true, writeAccess = true, registerDesc = "Watchdog timeout")
