declareRegister(peripheralName = WDT, registerName = INTENSET, addressOffset = 0x304'u32, readAccess = true, writeAccess = true, registerDesc = "Enable interrupt")
declareField(peripheralName = WDT, registerName = INTENSET, fieldName = TIMEOUT, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Write '1' to enable interrupt for event TIMEOUT")
declareRegister(peripheralName = WDT, registerName = INTENCLR, addressOffset = 0x308'u32, readAccess = true, writeAccess = true, registerDesc = "Disable interrupt")
